export interface ZoomingPluginOptions {
    selector: string;
    options: {
        bgColor?: string;
        bgOpacity?: number;
        closeOnWindowResize?: boolean;
        customSize?: string | {
            width: number;
            height: number;
        };
        enableGrab?: boolean;
        preloadImage?: boolean;
        scaleBase?: number;
        scaleExtra?: number;
        scrollThreshold?: number;
        transitionDuration?: number;
        transitionTimingFunction?: string;
        zIndex?: number;
    };
    delay: number;
}
